import './Modal.css';
import { useState } from "react";


const Modal = ({closeModal, onSubmit}) => {

    const [formState, setFormState] = useState(
          {
          ticketTypes: "",
          description: "",
        }
      );

      const [errors, setErrors] = useState('')
      const validateForm = () => {
            if (formState.ticketTypes) {
                setErrors('');
                return true;
                }

                else {
                    let errorFields = [];
                    for (const [key, value] of Object.entries(formState)) {
                      if (!value) {
                        errorFields.push(key);
                      }
                    }
                    setErrors(errorFields.join(", "));
                    return false;
      }

    const handleTicketChange = (e) => {
        setFormState({
            ...formState,
            ticketTypes: e.target.value,
        });
    }

    const handleChange = (e) => {
        setFormState({ ...formState, [e.target.name]: e.target.value });
      };


      const handleSubmit = (e) => {
        if (!validateForm()) return;
        e.preventDefault();

        onSubmit(formState);

        closeModal();
      };

    return ( 
        <div className='modal-container' onClick= {(e)=> {

            if(e.target.className === 'modal-container') closeModal();
        }}>

            <div className='modal'>

                <form action="">

                    <div className='form-group'>

                        <label htmlFor="ticketTypes">Ticket Types</label>
                        <input name='ticketTtypes' value={formState.ticketTypes} onChange={handleTicketChange}/>

                    </div>
                    <div className='form-group'>

                        <label htmlFor="description">Description</label>
                        <textarea name='description' value={formState.description} onChange={handleChange}/>

                    </div>
                    {errors && <div className="error">{`Please include: ${errors}`}</div>}
                    <button type='submit' className='btn' onClick={handleSubmit}>
                        Submit
                    </button>
                </form>
            </div>
        </div>
     );
}}
